mapper.select:

SELECT q.`id`, q.`quality_id`, q.`section_id`, q.`station_id`, q.`sub_id`,q.`responsible`, q.`staff_name`, q.`description`, q.`uptime`, q.`active`,q.`url`,q.`title`,a.`quality_category` as quality_name,s.`station_name` as station_name,l.`subcontractors_name` as `sub_name` FROM `smart_site`.`qualityshow` q
INNER JOIN `smart_site`.`risk` a ON q.`quality_id`=a.`id`
INNER JOIN `smart_site`.`station` s ON q.`station_id`=s.`id`
inner JOIN `smart_site`.`subcontractors` l ON q.`sub_id`=l.`id`

    q.`section_id`=`section_id`

        and q.`quality_id`=#{quality_id}


        and q.`staff_name` like concat("%",#{staff_name},"%")


        and q.`station_id`=#{station_id}


limit #{page},#{pageSize}

SELECT COUNT(*) FROM `smart_site`.`qualityshow`

    `section_id`=`section_id`

        and `quality_id`=#{quality_id}


        and `staff_name` like concat("%",#{staff_name},"%")


        and `station_id`=#{station_id}
