mapper.select:

SELECT  q.`id`, q.`section_id`, q.`knowledge_id`, q.`specialty_id`, q.`questions_type`, q.`questions_content`, q.`select_A`, q.`select_B`, q.`select_C`, q.`select_D`, q.`select_answer`, q.`judge_answer`, q.`gap_answer`, q.`short_answer`,k.`knowledge_name` as `knowledge_name` ,s.`specialty_name` as `specialty_name` FROM `smart_site`.`questions` q
INNER JOIN `smart_site`.`knowledge` k ON q.`knowledge_id`=k.`id`
INNER JOIN `smart_site`.`specialty` s ON q.`specialty_id`=s.`id`

    q.`section_id`=#{section_id}

        and q.`knowledge_id`=#{knowledge_id}


        and q.`specialty_id`=#{specialty_id}


        and q.`questions_type`=#{questions_type}


limit #{page},#{pageSize}

SELECT COUNT(*) FROM `smart_site`.`questions`

    `section_id`=#{section_id}

        and `knowledge_id`=#{knowledge_id}


        and `specialty_id`=#{specialty_id}


        and `questions_type`=#{questions_type}
