mapper.select:

SELECT r.`id`, r.`title`, r.`section_id`, r.`station_id`, r.`sub_id`, r.`staff_name`, r.`responsible`, r.`up_time`,r.`plan_time`, r.`description`, r.`active`, l.`station_name` as `station_name`, f.`subcontractors_name` as `sub_name` FROM `smart_site`.`riskrectify` r
INNER JOIN `smart_site`.`station` l ON r.`station_id`=l.`id`
INNER JOIN `smart_site`.`subcontractors` f ON r.`sub_id`=f.`id`
WHERE r.`section_id`=#{section_id} and r.`station_id`=#{station_id}
order by r.`up_time` desc
limit #{page},#{pageSize}
