mapper.select:

SELECT r.`id`, r.`risk_id`, r.`staff_id`, r.`process_id`, r.`section_id`, r.`station_id`, r.`sub_id`, r.`responsible`, r.`description`, r.`uptime`, r.`url`, r.`active`,r.`title`,a.`risk_category` as risk_name,s.`station_name` as station_name,l.`subcontractors_name` as `sub_name`,p.`process_name` as `process_name`,z.`staff_name`  as `staff_name` FROM `smart_site`.`riskshow` r
INNER JOIN `smart_site`.`risk` a ON r.`risk_id`=a.`id`
INNER JOIN `smart_site`.`station` s ON r.`station_id`=s.`id`
INNER JOIN `smart_site`.`subcontractors` l ON r.`sub_id`=l.`id`
INNER JOIN `smart_site`.`process` p ON r.`process_id`=p.`id`
INNER JOIN `smart_site`.`staff` z ON r.`staff_id`=z.`id`

    r.`section_id`=#{section_id}

        and r.`risk_id`=#{risk_id}


        and r.`station_id`=#{station_id}


limit #{page},#{pageSize}

SELECT COUNT(*) FROM `smart_site`.`riskshow`

    `section_id`=#{section_id}

        and `risk_id`=#{risk_id}


        and `station_id`=#{station_id}
